				//	+ Pid_94;
		//	int UptoGpsFix = uptoMillage + MILLAGE + LITER_PER_HOUR + NetworkOperator + M_C_C + M_N_C + LOCATION_CODE
			//		+ Cell_id;
			int i=0;
